SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces  recepteurs a activite enzymatique appartiennent a la &lt;/span&gt;&lt;span &gt;famille de proteines transmembranaires qui associent une fonction de liaison du mediateur et une fonction enzymatique effectrice. plusieurs activites enzymatiques ont ete observees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
