SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons dans un premier temps aborder les recepteurs cytosoliques nommes aussi recepteurs nucleaires. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'agit d'une famille de proteines qui se lient a la region promotrice des genes soit pour augmenter soit reprimer leur transcription en arn messagers. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
