SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide2.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les recepteurs &lt;/span&gt;&lt;span  fontweight='bold'&gt;nucleaires &lt;/span&gt;&lt;span &gt;sont des facteurs de transcription particuliers, actives par des ligands lipophiles, souvent de nature lipidique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces ligands peuvent etre des hormones circulantes, ou encore des mediateurs synthetises par la cellule elle-meme, ou encore les metabolites cytosoliques d'un mediateur extracellulaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;certains recepteurs nucleaires sont presents dans le cytoso²l et apres liaison du ligand ils vont migrer dans le noyau.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe de fait une sorte d'ambiguite dans les termes entre les recepteurs nucleaires des l'origine et les recepteurs cytosoliques qui deviennent nucleaires apres fixation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les recepteurs nucleaires, comme les autres facteurs de transcription peuvent etre actives par phosphorylation  assuree par les proteines kinases des voies de signalisation des recepteurs membranaires :  vous vous souvenez de la sequence ras / raf / mek / erk des recepteurs a activite tyrosine kinase&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les recepteurs nucleaires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la superfamille des recepteurs nucleaires comprend 49 recepteurs dont 21 sont dits orphelins (ou orphans dans ce tableau dont la presence ici est uniquement illustrative : merci de ne pas perdre de temps a apprendre ce type de liste).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un recepteur est dit orphelin quand la substance endogene qui l'active n'est pas connue. ces recepteurs sont cependant autant de promesses de nouvelles strategies therapeutiques &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les recepteurs nucleaires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les recepteurs nucleaires sont constitues d'un enchainement de 410 a 1000 acides amines, repartis en 6 domaines nommes a, b, c, d, e et f.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le domaine a-b est de longueur et de structure tres variable d'un recepteur a l'autre, il comporte un motif af-1 qui lie des co-activateurs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le domaine c est le domaine de liaison a l'adn : il est au contraire du precedent tres bien conserve et se caracterise par un double repliement : ce sont ce qu'on appelle des motifs en doigts de zinc.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les recepteurs nucleaires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les interactions entre les recepteurs nucleaires et l'adn se font selon deux modalites :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la premiere concerne par exemple les recepteurs des hormones steroides surrenaliennes : en absence de ligand le recepteur est associe a des proteines nommees hsp, pour heat shock proteins car l'expression de ces proteines est augmentee en condition de stress comme le stress hyperthermique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la fixation du ligand au recepteur forme un nouveau complexe (sans hsp), ce complexe va se dimeriser pour se lier a l'adn et activer ou reprimer la transcription.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la deuxieme modalite concerne par exemple les recepteurs des hormones thyroidiennes : en absence de ligand le recepteur est fixe a l'adn avec une affinite elevee. la fixation du ligand au recepteur active la transcription.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour illustrer la pharmacologie de ces recepteurs nucleaires je prendrais seulement ces deux exemples : les recepteurs des hormones steroides surrenaliennes et les recepteurs des hormones thyroidiennes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
