SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;commencons donc par les recepteurs des hormones steroides surrenaliennes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces recepteurs sont nommes mr, gr, ar, er et pr &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;avec mr pour les recepteurs aux mineralocorticoides dont le ligand endogene est l'aldosterone un mineralocorticoide et acteur important du chapitre de physiologie renale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;avec gr pour les recepteurs aux glucocorticoides dont le ligand endogene est le cortisol, un glucocorticoide.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;avec ar pour les recepteurs aux androgenes, dont le ligand endogene est l'hormone sexuelle male la testosterone.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;avec er pour les recepteurs aux estrogenes dont le ligand endogene est l'hormone sexuelle femelle le 17 &lt;/span&gt;&lt;span &gt;b&lt;/span&gt;&lt;span &gt;-estradiol.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et enfin avec pr pour les recepteurs aux progestagenes dont le ligand endogene est l'hormone sexuelle femelle la progesterone.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;toutes les hormones steroides sont synthetisees a partir du cholesterol, elles possedent une structure tres proches mais des fonctions tres variees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour illustrer cette famille de recepteurs je ne vais choisir la encore qu'un exemple  les recepteurs gr.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les recepteurs gr@";
SDpagedocid[SDpagenum] = SDdocnum;
