SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les rx gr sont issus d'un gene unique : ce gene code pour deux proteines gra et grb.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;gra est considere comme etant le rx fonctionnel des corticoides.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il est present dans tous les tissus, il regule la transcription de nombreux genes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le principal corticoide endogene est, nous l'avons dit le cortisol, synthetise majoritairement dans la zone fasciculee des glandes surrenales.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les effets du cortisol sont multiples : il augmente la synthese des lipocortines ou annexines qui sont des proteines intracellulaires qui fixent le calcium et sont impliquees dans des phenomenes qui impliquent des interactions de type membrane-membrane comme l'exocyose …. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il augmente aussi la synthese d'enzymes de nombreuses voies metaboliques : stimulent la neoglycogenese, stimulent le catabolisme des proteines, et redistribuent les graisses au niveau de la face et du cou.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le cortisol diminue la synthese de tnfa et de il1, diminue aussi la synthese des cyclo-oxygenase et de no-synthase.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les corticoides@";
SDpagedocid[SDpagenum] = SDdocnum;
