SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide8.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les corticoides utilises en therapeutique, aussi appeles glucocorticoides en rapport avec leur effet sur le metabolisme glucidique, sont des agonistes du rx gr&lt;/span&gt;&lt;span &gt;a&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce sont des anti-inflammatoires utilises dans de nombreuses indications sous de multiples formes galeniques : en inhalation dans l'asthme et la bpco,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en gouttes ou spray nasals dans le tt des  rhinites allergiques,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en collyres contre les inflammations oculaires, en creme ou pommade contre l'eczema, ou la dermatite, en infiltration intra-articulaires par exemple ou encore par voie orale dans certains etats inflammatoires severes ou comme immunosuppresseur a la suite de transplantations. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour le moment les antagonistes du gra n'ont pas d'indications therapeutiques. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

