SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si nous cherchons dans le dictionnaire vidal qui regroupe les monographies de toutes les specialites commercialisees en france, nous triovons dans le paragraphe qui traite de la pharmacodynamie du becotide : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;autres medicaments pour les syndromes obstructifs des voies aeriennes par inhalation (code atc : r03ba01).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;glucocorticoide par voie inhalee, anti-asthmatique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le dipropionate de beclometasone en inhalation exerce un effet anti-inflammatoire essentiellement local sur la muqueuse bronchique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

