SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les differents phenomenes electriques cardiaques sont responsables de variations du potentiel electrique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le cœur peut etre assimile a un dipole que l'on peut enregistrer a distance du cœur avec des electrodes receptrices situees a differents endroits de la surface du corps.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un courant se dirigeant vers les electrode s'inscrira comme une onde &lt;/span&gt;&lt;span  textdecoration='underline'&gt;positive&lt;/span&gt;&lt;span &gt; sur l'electrocardiogramme alors qu'un courant s'eloignant des electrodes sera represente par une onde &lt;/span&gt;&lt;span  textdecoration='underline'&gt;negative&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
