SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'electrocardiogramme dit de surface n'est que l'integration et la sommation de tous les phenomenes electriques que nous avons decris dans le chapitre precedent.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sur cette figure, vous avez represente la progression des pa le long du massif cardiaque et la sommation du signal que l'on enregistre en surface en bas, qui est la correspondance sur l'electrocardiogramme des memes evenements electriques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous pouvez deja constater que le signal de l'ecg est totalement different des potentiels d'action.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
