SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vous vous souvenez que l'onde de depolarisation (ici representee en rouge) nait dans le toit de l'oreillette droite et se propage jusqu'aux deux ventricules.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous voyez ici une autre representation de la correspondance entre l'evenement electrique cardiaque et le recueil du signal sur le trace electrocardiographique de surface. nous reviendrons plus en detail sur les differentes ondes plus tard dans ce cours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
