SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;on distingue deux types de derivation : les derivations peripheriques et les derivations precordiales&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les derivations peripheriques ou frontales explorent le cœur dans le plan frontal. on parle aussi des derivations des membres car obtenues a partir d'electrodes places au niveau des bras et des jambes&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les derivations precordiales explorent le cœur dans un plan transversal &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
