SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les derivations peripheriques sont divises en derivations unipolaires et en derivations bipolaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les derivations &lt;/span&gt;&lt;span  fontweight='bold'&gt;bipolaires&lt;/span&gt;&lt;span &gt; proviennent de la difference de potentiel electrique entre &lt;/span&gt;&lt;span  fontweight='bold'&gt;deux membres&lt;/span&gt;&lt;span &gt;; la difference de potentiel est lue du negatif (-) vers le positif (+);&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;di&lt;/span&gt;&lt;span &gt; : du bras droit (-) vers le bras gauche (+);&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;dii&lt;/span&gt;&lt;span &gt; : du bras droit (-) vers la jambe gauche (+);&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;diii&lt;/span&gt;&lt;span &gt; : du bras gauche (-) vers la jambe gauche (+).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les derivations unipolaires, plutot que de mesurer la difference de potentiel entre deux electrodes, renvoient l'activite electrique de l'electrode par rapport a un point central qui correspond en quelque sorte au cœur;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'electrode represente le pole positif (+) alors que le point central represente le pole negatif (-);&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la difference de potentiel est lue la aussi du negatif (-) vers le positif (+);&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la lecture ainsi obtenue doit etre amplifiee pour etre lue sur le trace, d'ou le « a » devant chacune de ces derivations; v pour voltage et r, l ou f pour respectivement les bras droit, gauche et la jambe gauche.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

