SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'ecg est un examen important sur le plan diagnostic&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;aussi doit-il etre enregistre dans des conditions standardisees&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un ecg s'enregistre couche et au repos : c'est dans ces conditions que l'on peut comparer les valeurs mesurees aux valeurs normales et que l'on peut parler de normalite ou non du trace electrocardiographique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
