SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;lorsque l'on parle de l'onde de depolarisation du ventricule, on emploie toujours l'acronyme qrs, quel que soit sa forme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette appellation n'est pas une description du complexe.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;or, selon la derivation et surtout selon une eventuelle pathologie cardiaque, la forme du qrs peut varier.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour le decrire il y a donc une nomenclature qui est la suivante&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la premiere onde negative du qrs est appelee une onde q&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'onde positive qui suit l'onde q est appelee une onde r&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une onde negative qui suit une onde r est appelee une onde s&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il peut parfois y avoir une deuxieme onde positive apres l'onde s, appelee onde r'&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en plus pour avoir une idee de l'amplitude des ondes on utilise une lettre minuscule si l'amplitude est inferieure ou egale a 5 mm et une lettre majuscule si l'amplitude est superieure a 5 mm. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans les exemples donnees sur la diapositive, le complexe qrs en haut entoure en vert a un aspect dit rs&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et le complexe qrs du bas, toujours entoure en vert, a un aspect dit rsr'.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
