SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'analyse de l'ecg et sa description doit partir du rythme cardiaque et de la genese du potentiel d'action jusqu'a la repolarisation des ventricules donc sur l'ensemble du trajet de l'onde de depolarisation cardiaque. mais il faut aussi analyser la repetition des ces evenements dans le temps donc d'un cycle a l'autre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsque vous lisez un electrocardiogramme, vous devez passez en revue tous les elements que nous avons decrit dans le chapitre precedent.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
