SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voici l'enregistrement 12 derivations standards d'un ecg normal.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut toujours verifier que la vitesse d'enregistrement et l'amplitude sont standardises &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; c'est ecrit en bas a gauche sous le trace &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; on peut aussi verifier l'amplitude avec le petit carre au debut de l'enregistrement que j'ai entoure en vert.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;conformement au canevas donne sur la diapositive precedente &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;voila comment on peut le decrire :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le rythme est sinusal a &lt;/span&gt;&lt;span  fontweight='bold'&gt;59&lt;/span&gt;&lt;span &gt;/min &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; cela signifie que l'onde de depolarisation provient du nœud sinusal&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'intervalle pr est normal a environ 0,16 s&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le complexe qrs est normal en amplitude et en duree (moins de 0,08 s)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il n'y a pas d'anomalie du segment st ou de l'onde t - on dit que la repolarisation est normale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;evidemment il existe toute une semiologie electrocardiographique qui s'eloigne du cadre de ce cours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous remarquerez que le point j est isoelectrique dans toutes les derivations alors qu'il est au dessus de la ligne isoelectrique en v3 et de facon moins evidente en v4.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;quelques exemples de differents traces vont maintenant vous etre presentes - ces traces vous permettent de controler que vous avez compris le principe et les informations visibles sur un trace - en aucun cas vous n'aurez de traces anormaux a interpreter pour le concours.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
