SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les objectifs de ce cours chapitre sont &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de connaitre l'organisation generale du systeme circulatoire avec les differentes circulations que sont la circulation systemique, la circulation pulmonaire et la circulation lymphatique;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de connaitre les differences histologiques qui sont a l'origine des differences fonctionnelles des vaisseaux et enfin&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de connaitre les grands principes de l'hemodynamique &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
