// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@le reseau vasculaire - organisation  generale@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide3.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'organisation generale du systeme vasculaire vous ai presente sur cette diapositive&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on le divise en trois comme nous l'avons deja vu &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  textdecoration='underline'&gt;la circulation systemique&lt;/span&gt;&lt;span &gt;, ici legendee sur le schema en noir, qui part de l'aorte, a la sortie du ventricule gauche pour se terminer par les veines caves qui s'abouchent dans l'oreillette droite et qui comprend les zones d'echange capillaire avec les organes; c'est une circulation dite a haute pression car les pressions qui regnent a l'interieur des arteres sont elevees&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  textdecoration='underline'&gt;la circulation pulmonaire&lt;/span&gt;&lt;span &gt;, qui est une circulation uniquement fonctionnelle dediee aux echanges gazeux, allant du tronc de l'ap jusqu'a l'abouchement des veines pulmonaires dans l'oreillette gauche; c'est un systeme dit a basse pression, en opposition avec le precedent, car les pressions qui regnent dans les arteres pulmonaires sont 4 a 5 fois plus basses; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a ces deux systemes, il faut ajouter un dernier systeme tres important, la circulation lymphatique - ce systeme est un systeme unique que l'on retrouve parallelement dans les deux systemes - il est ici legende en vert.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@le reseau vasculaire@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide4.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'homeostasie des organes depends de l'existence et de la fonction d'un reseau de vaisseaux : un reseau sanguin comportant les arteres, les veines et des capillaires et un reseau lymphatique comportant uniquement des capillaires et des vaisseaux lymphatiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le systeme arterio-veineux transporte des gaz, des nutriments, des metabolites et des cellules sanguines.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le systeme lymphatique, inversement, est un systeme borgne, naissant des tissus et comporte outre des capillaires et des vaisseaux lymphatiques, des ganglions lymphatiques et est associe aux organes lymphoides &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; ce systeme parallele et couple au systeme vasculaire, rapporte des macromolecules, des lipides, des cellules immunitaires et un fluide, le liquide lymphatique, dans la circulation vasculaire systemique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

