SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la structure histologique est differente d'un vaisseau a l'autre et d'une circulation a une autre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une partie du systeme vasculaire joue un role de transport et de controle hemodynamique et une autre partie sera dediee aux interactions avec les organes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;interessons nous d'abord a la structure des vaisseaux qui n'ont que ce role de transport et de de controle hemodynamique, et qui sont materialises dans le bas de cette diapositive. bien sur en controlant le debit vasculaire local, ces vaisseaux peuvent indirectement influence le fonctionnement du reseau capillaire sous-jacent mais ils n'ont pas d'interaction directe avec les echanges metaboliques au niveau des organes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
