SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'endothelium vasculaire intervient ainsi de facon majeur dans le controle du tonus vasculaire, de la vasomotricite et de la permeabilite arterielle en synthetisant de nombreux mediateurs vasodilatateurs (surtout le no ou monoxyde d'azote) et vasoconstricteurs (sur l'endotheline1) en reponse a des stimuli chimiques, mecaniques ou nerveux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il intervient dans l'angiogenese c'est-a-dire dans la creation ou la reparation des vaisseaux en synthetisant du vegf.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il a un role antithrombotique c'est-a-dire qu'il exprime a sa surface des molecules qui empechent le sang de coaguler a l'interieur des vaisseaux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
