SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vous pouvez voir sur ces calculs simples l'influence majeure de ces parametres sur le debit sanguin.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a gauche, pour un meme rayon vous pouvez voir la variation de debit selon le gradient de pression et a droite, l'influence du diametre du vaisseau sur le debit pour un meme gradient de pression. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez representer la un facteur majeur de controle du debit vasculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
