SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce schema general est fondamental. il represente a la fois le niveau de pa (en mmhg) ainsi que le regime de pression (pulsatile ou continu) le long du lit vasculaire depuis le vg jusqu'au vd.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous pouvez voir que le cœur met en charge le volume sanguin. comme vous le savez la pression qui regne dans le vg ici oscille entre un maximum et un minimum proche de 0 mmhg.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce regime de pression est totalement different du regime de pression dans le systeme vasculaire qui ne revient par exemple plus a 0 mmhg, et surtout le niveau de pression va progressivement s'abaisser (il va s'amortir) pour n'etre plus que de quelques mmhg dans les grosses veines avant que le ventricule droit ne remette en charge le volume sanguin avant de passer dans la circulation pulmonaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
