SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;parallelement aux variations de la pression arterielle et du regime de pression, les caracteristiques varient en tout point du systeme vasculaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez represente sur ce schema quelques elements avec en haut la surface du systeme qui presente evidemment une importante surface d'echange au niveau des capillaires et en bas la vitesse circulatoire qui degringole dans les arterioles et qui est presque nulle dans les capillaires evidemment pour favoriser encore une fois les echanges avec les tissus..&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
