SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le plan est court mais le developpement et la mise en place des differents parametres sera plus longue.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je ne peux que vous conseiller pour ce chapitre de comprendre et de visualiser les phenomenes mais de ne pas apprendre par cœur la succession des evenements car c'est une source importante d'erreurs. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
