SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons d'abord decrire le cycle cardiaque sur le plan hemodynamique cette fois c'est-a-dire en terme de volume et de pression intracavitaire. il existe plusieurs facons de le decrire. une premiere plutot anatomique, une deuxieme qui analyse dans le temps la superposition des evenements et permet de comprendre les mouvements des volumes sanguins et enfin une representation plus fonctionnelle qui nous permettra de comprendre les facteurs de controle du debit cardiaque.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
