SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le cœur a un fonctionnement cyclique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un cycle &lt;/span&gt;&lt;span &gt;peut se definir comme une serie de phenomenes qui se suivent dans un ordre determine.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  textdecoration='underline'&gt;le cycle cardiaque&lt;/span&gt;&lt;span &gt; comprend 2 phases principales, initiees par les evenements electriques que vous connaissez maintenant :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;  - la &lt;/span&gt;&lt;span  textdecoration='underline'&gt;diastole&lt;/span&gt;&lt;span &gt; qui &lt;/span&gt;&lt;span &gt;est la&lt;/span&gt;&lt;span &gt; phase de &lt;/span&gt;&lt;span &gt;relaxation &lt;/span&gt;&lt;span &gt;musculaire pendant laquelle une cavite cardiaque se remplit&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;  - la &lt;/span&gt;&lt;span  textdecoration='underline'&gt;systole&lt;/span&gt;&lt;span &gt; qui est la&lt;/span&gt;&lt;span &gt; phase de &lt;/span&gt;&lt;span &gt;contraction&lt;/span&gt;&lt;span &gt; musculaire pendant laquelle le volume intra cavitaire est ejecte&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour des raisons pedagogiques, nous allons decrire les evenements successifs qui surviennent dans le ventricule gauche, mais il faut garder a l'esprit que les memes evenements surviennent aussi dans le ventricule droit et qu'il existe evidemment un synchronisme entre les deux ventricules.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut aussi garder a l'esprit que les oreillettes sont des reservoirs qui se remplissent en permanence, qu'elles presentent aussi un cycle remplissage-ejection mais qui precede normalement le cycle ventriculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, il faut comprendre que tous les mouvements liquidiens intracavitaires sont lies a des gradients hemodynamiques de pression, les volumes se deplacant des zones a haute pression vers des zones a plus basse pression. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comprendre l'evolution des pressions, c'est comprendre le cycle cardiaque.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
