SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;avant de decrire le cycle phase par phase je vous presente ici un modele non anatomique d'un cœur droit ou gauche &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il comprend &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une chambre d'admission a basse pression &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; les oreillettes qui sont des reservoirs &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; cette chambre se remplit en permanence mais avec des debits evidemment variables selon le moment du cycle&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une valve dite d'admission qui est la valve mitrale pour le cœur gauche et la valve tricuspide pour le cœur droit &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; cette valve s'ouvre pour remplir le ventricule &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; ces valves, comme toutes les valves sont unidirectionnelles c'est a dire qu'elles ne peuvent s'ouvrir que dans un sens. on parle aussi de valve auriculo-ventriculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le ventricule lui meme qui est une cavite situe entre la valve d'admission et&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la valve d'ejection qui est la voie de sortie du ventricule. l'ejection se fait dans les gros vaisseaux de la base du cœur. il s'agit de la valve aortique et de l'aorte pour le ventricule gauche et de la valve pulmonaire et du tronc de l'artere pulmonaire pour le ventricule droit. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
