SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;commence alors le remplissage ventriculaire. le sang s'ecoule de l'oreillette gauche dans le ventricule gauche.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le volume ventriculaire augmente mais la pression intraventriculaire gauche n'augmente que peu. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pendant ce temps, l'oreillette s'etant videe, la pression intra-auriculaire diminue ce que l'on a du mal a voir sur le schema car les pressions sont tres proches&lt;/span&gt;&lt;span &gt;…&lt;/span&gt;&lt;span &gt; la pression aortique baisse progressivement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette premiere phase du remplissage ventriculaire est dite passive car ne se faisant que par le jeu des gradients de pression&lt;/span&gt;&lt;span &gt;…&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

