SDpagenum++;
SDpagetitle[SDpagenum] = "@remplissage ventriculaire (actif)@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide11.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;survient alors la contraction de l'oreillette qui en augmentant la pression intra-auriculaire gauche permet de remplir encore le ventricule gauche d'un petit volume sanguin. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par definition, le volume sanguin qui est contenu a ce moment dans le ventricule gauche est le volume telediastolique ventriculaire gauche (volume de fin de diastole, de fin de remplissage) et la pression intra-ventriculaire est la pression telediastolique du ventricule gauche, ou ptdvg&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@contraction isovolumique@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
