SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la valve mitrale est fermee et la valve aortique n'est pas encore ouverte.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le ventricule gauche est isole de l'oreillette et de l'aorte &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; c'est donc une phase isovolumique &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le ventricule gauche se depolarise et se contracte &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; sa pression augmente rapidement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette phase de contraction isovolumique est en rapport avec les flux calciques entrants que nous avons decrit dans le chapitre precedent.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
