SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la pression ventriculaire gauche augmente.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;quand elle depasse la pression minimale aortique (la pression diastolique aortique), la valve aortique s'ouvre et une partie du volume sanguin est ejectee dans l'aorte : c'est l'ejection ventriculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le volume qui reste dans le ventricule gauche est appele la volume telesystolique vg et la pression, la pression telesystolique ventriculaire gauche.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@catheterisme cardiaque gauche@";
SDpagedocid[SDpagenum] = SDdocnum;
