SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la definition de la precharge est assez facile a comprendre, mais ses determinants sont multiples.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on la definit comme etant l'ensemble des facteurs qui contribuent a la grandeur du volume telediastolique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le systeme cv etant un circuit ferme, on peut l'assimiler a tout ce qui revient au niveau du cœur droit c'est a dire le volume du retour veineux&lt;/span&gt;&lt;span &gt;…&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
