SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la loi fondamentale du cœur, ou loi de frank starling, est une propriete intrinseque du myocarde.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elle dit que, dans les limites physiologiques, et si j'interprete les propos de starling rapportes sur la diapositive, plus le cœur se remplit, plus il ejecte. &lt;/span&gt;&lt;span &gt;le cœur rend toujours ce qu'il recoit.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sur le plan mecanistique, on peut faire une analogie avec la relation tension/longueur deja vue dans le muscle strie squelettique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt;tirement des fibres myocardiques aboutit a une optimisation des ponts actine/myosine permettant lors de la contraction une augmentation de la force de contraction qui y est proportionnelle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

