SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les determinants de la precharge sont nombreux&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez ici une liste non exhaustive des facteurs qui peuvent la modifier.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'agit de tous les facteurs de variations du retour veineux que nous reverrons dans le cours de physiologie vasculaire et de la compliance du ventricule donc de ce que l'on peut appeler sa performance a se remplir pendant la diastole, donc sa performance diastolique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on gardera aussi a l'esprit que la geometrie des cavites cardiaques qui peuvent varier de facon importante influence aussi la precharge ventriculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour moi : stress = pr/2h&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
