SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les determinants de la post-charge sont essentiellement en rapport avec des proprietes du systeme vasculaire que sont les resistances vasculaires peripheriques et tout leurs facteurs de variations que nous reverrons dans un chapitre ulterieur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
