SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voici le potentiel d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;augmentation du travail cardiaque qui constitue la capacite de reserve cardiaque physiologique, cad les capacites d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;adaptation de la fonction pompe aux besoins en augmentant la precharge et la post-charge : nous sommes partis d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;en bas a g et nous pouvons atteindre les limites en-haut a d.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la courbe verte de remplissage, represente la relation p/v diastolique passive (ou compliance) du vg obtenue a differents niveaux de precharge &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; c'est l'elastance passive, que l'on a appeler avant la performance diastolique. comme vous le voyez la relation n'est pas lineaire mais au dela d'un certain niveau de precharge, la pression telediastolique vg peut augmenter fortement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la droite noire qui relie tous les points de fermeture aortique, represente la relation p/v active du vg et reflete la reponse contractile du myocarde : c'est l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;e&lt;/span&gt;&lt;span &gt;lastance active telesystolique emax que l'on peut calculer a partir du ves, du vm et de la pts du vg.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@la contractilite myocardique@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide33.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la contractilite myocardique (donc la fonction inotrope) est une propriete intrinseque des fibres myocadiques isolees, independamment des conditions de charge. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est donc une mesure difficile en pratique surtout chez l'homme. on peut la mesurer en calculant l'&lt;/span&gt;&lt;span &gt;elastance active, notion que nous allons redefinir, mais en pratique on l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;approche avec la mesure facile a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;aide de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;echographie cardiaque, de la fraction d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;ejection. on gardera toutefois a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;esprit que la fe mesuree in vivo dependra du niveau de precharge et de postcharge au moment de la mesure.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le controle de la contractilite est a la fois mecanique &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; c'est la loi fondamentale du cœur mais aussi par le sna et hormonal.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@contractilite@";
SDpagedocid[SDpagenum] = SDdocnum;
