SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il est possible d'augmenter la contractilite &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; cela se traduit par une augmentation de la pente de l'&lt;/span&gt;&lt;span &gt;elastance active.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;inversement une baisse de la contractilite, que l'on peut observer en pathologie, se traduit par une diminution de la pente de cette relation&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
