// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@le debit cardiaque@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide37.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le debit cardiaque est la quantite de sang ejecte par le ventricule par unite de temps. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est une mesure globale de la performance du systeme cardiovasculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le cœur fonctionnement en serie, les debit des ventricules gauche et droit sont bien sur equivalent&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;au repos le debit cardiaque est en moyenne de 5 l/min variant selon la surface corporelle de 4 a 7 l/min&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour comparer les debits cardiaques entre les sujets, on utilise donc l'index cardiaque qui est la quantite de sang ejectee par unite de temps et par metre de surface corporelle. il est alors de 3 a 3,5 l/min/m2.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@facteurs de controle du debit cardiaque@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide38.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les facteurs de controle du debit cardiaque sont multiples.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez schematisez ici le cœur au centre qui se remplit (la precharge) et qui se contracte contre une resistance (la contractilite et la postcharge)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en vert vous avez les elements de controle du debit cardiaque et en blanc quelques facteurs permettant encore de moduler chaque parametre. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le debit cardiaque n'est pas une valeur constante. il ne fait pas l'objet d'une regulation mais d'un simple controle qui vise a l'adapter aux besoins. il s'adapte en permanence quasiment battement par battement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
