SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce cours est dense et les concepts exposes peuvent etre assez abstraits. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les exemples que l'on donnera apres le cours de physiologie vasculaire permettra de comprendre des interactions permanentes entre tous ces parametres. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut comprendre que le cœur s'adapte cycle a cycle et que le comportement des ventricules depend a la fois de parametres physiologiques mais aussi de parametres hemodynamiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;tous ces parametres permettent de moduler le debit cardiaque dont le niveau est fixe par la debit du retour veineux donc par les besoins tissulaires des organes peripheriques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;merci pour votre attention et bon courage !&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
