SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous avons vu dans le chapitre precedent que le cœur etait automatique, c'est a dire qu'il generait spontanement et regulierement des potentiels d'action qui se propageaient depuis le nœud sinusal jusqu'au reseau de purkinje a toutes les cellules myocardiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsque les potentiels d'action arrivent au niveau des cardiomyocytes ils declenchent leur contraction. c'est ce que l'on appelle le couplage excitation-contraction.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
