SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;dans ce chapitre nous allons etudie les differents evenements qui aboutissent a la contraction puis a la relaxation de la cellule musculaire cardiaque. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces phenomenes sont lies principalement a des mouvements calciques rapides intracellulaires controles de differentes facons.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de nombreuses analogies pourront etre faites avec la contraction d'un muscle strie squelettique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
