SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;vous devez connaitre parfaitement la structure histologique du myocarde car elle permet deja de comprendre le fonctionnement du muscle&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les differentes structures sont identiques a ce que l'on peut voir dans le muscle strie squelettique et je considere tous ces elements comme acquis.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans les prochaines diapositives, nous allons surtout nous interesser aux evenement qui surviennent le long des tubules t qui sont ces invaginations de la membrane plasmique qui ont une proximite essentielle avec des invaginations du reticulum sarcoplasmique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
