// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les mouvements calciques intracellulaires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les acteurs qui jouent un role fondamental dans le cycle calcique intracellulaire sont representees sur cette figure. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les cycles contraction/relaxation des cardiomyocytes sont controles par les changements rapides de concentration calcique intracellulaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il faut que la concentration calcique augmente de facon rapide et importante pour activer les interactions entre les proteines contractiles mais de facon tout aussi importante, il faut que la concentration calcique baisse de facon rapide et significative pour que les interactions cessent et que le cœur se relache.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en etat stable, le flux entrant calcique est strictement identique au flux sortant.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;evidemment, le calcium n'agit pas seul mais de facon concomitante a la production d'atp. le cycle contraction-relaxation depend donc aussi des phosphorylations oxydatives donc du metabolisme mitochondrial.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous allons decrire successivement les differentes etapes de ces mouvements.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@ouverture des canaux calciques  de type-l@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide8.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'arrivee du potentiel d'action le long de la membrane plasmique parcourt les tubules t et provoquent l'ouverture des canaux calciques de type l que l'on appelle aussi recepteurs aux dihydropyridines. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il se produit alors une augmentation de la concentration calcique dans l'espace situe entre la membrane du sarcolemme et la membrane du reticulum sarcoplasmique &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; il existe une parfaite concordance spatiale entre les recepteurs aux dihyropyridines et des recepteurs situes sur la membrane du reticulum, les recepteurs a la ryanodine qui sont de type 2 dans le muscle cardiaque (alors qu'elle sont de type 1 dans le muscle strie squelettique).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@relargage du ca2+ induit par le calcium@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide9.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la fixation du calcium par les recepteurs a la ryanodine va entrainer la liberation des stocks calciques fixees sur la calsequestrine dans le rs&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on observe donc une rapide augmentation de la concentration calcique que l'on nome d'ailleurs une etincelle calcique ou ca2+ spark des anglo saxons.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@la contraction@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide10.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il s'en suit la contraction des cardiomyocytes&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les evenements qui aboutissent a la contraction sont similaires a ceux survenant lors d'une contraction du muscle strie squelettique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le calcium se fixe sur la troponine c&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cela entraine une modification de la configuration stereochimique de la tropomyosine qui normalement empeche l'interaction actine-myosine&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;celle-ci peut donc se produire, la tete de myosine se deplace, le sarcomere se raccourcit, les stries z se rapprochent : il y a contraction &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@recapture du ca2+ - la relaxation@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide11.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;apres la depolarisation, la membrane se repolarise, les canaux calciques membranaires vltage dependant sont fermes, et on assiste a une diminution de la concentration calcique intracellulaire du fait de la recapture par les serca, qui sont des pompes ca2+ atpase du reticulum et a une explusion du ca2+ par les pompes membranaires ncx qui expulsent 1 ion calicum contre 3 ions sodium &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; ce desequilibre ionique est comble par les pompes na/k qui sont localises avec les pompes ncx (na/ca exchanger)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@controle des flux calciques - contraction@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide12.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en controlant l'amplitude et la duree des flux calciques, on peut faire varier l'intensite et la duree des cycles contraction-relaxation. par exemple a l'exercice, les transferts calciques sont plus plus importants et rapides sous l'effet de la stimulation beta-adrenergique ; inversement lorsque ces flux sont alterees, ils peuvent etre a l'origine de maladies du muscle cardiaque.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plusieurs phenomenes peuvent controler les flux calciques entrants :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la taille du flux calcique a travers les canaux calciques de type l&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la sensibilite des recepteurs a la ryanodine au calcium&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'importance du pool calcique stocke dans le reticulum sarcoplasmique sur la calsequestrine &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; c'est un element majeur et tres puissant de controle puisque en moyenne l'amplitude du flux calcique est proportionnel au cube du contenu &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; la quantite de calcium contenu dans le rs est tout simplement controle par des boucles de retrocontrole sur les canaux calciques de type l (qui gere le flux calcique entrant) et les pompes sodium/calcium (qui gere les flux sortant) &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; en jouant sur une difference entre les deux flux, on peut moduler rapidement le pool calcique du rs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@controle des flux calciques - relaxation@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide13.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous avons vu comment pouvait etre controle les flux calciques entrant et donc comment pouvait etre controlee la force de contraction.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;voyons maintenant comment sont controlees les flux calciques pendant la repolarisation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour que le muscle cardiaque se relache bien (on parle pour cette propriete de caractere lusitrope), il faut que la concentration calcique intracellulaire redevienne basse.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le controle des mecanismes n'est pas totalement compris chez l'homme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on peut toutefois dire &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;que l'activite de la pompe ncx sodium/calcium est proportionnelle a la concentration calcique intracellulaire (donc plus la concentration est importante et plus le calcium sortira de la cellule par cette pompe)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;que le controle se fera donc indirectement par le flux entrant de calcium pendant la depolarisation &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; plus il en entrera, plus il en sortira&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;bien sur les serca contribuent a ces flux mais a priori ne jouent pas de role majeur dans le controle de la concentration calcique intracellulaire &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

