SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;bonjour a tous &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;maintenant que vous possedez toutes les connaissances de physiologie cardio-circulatoire, vous pouvez sans probleme vous interessez a la facon dont s'adapte le systeme cardio-circulatoire en reponse a un stress, a un environnement particulier : c'est ce que l'on appelle la physiologie integree. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plus tard dans votre cursus, vous pourrez aussi comprendre les consequences d'un dysfonctionnement de ce systeme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;j'ai choisi de vous parler d'une situation frequente, quotidienne : l'adaptation cardiovasculaire a l'effort.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
