SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les objectifs de ce chapitre est &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'integrer que tout commence par une variation de la demande metabolique musculaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de connaitre les principaux mecanismes d'adaptation du systeme cardiocirculatoire pour permettre l'augmentation de debit cardiaque qui sous-tend l'augmentation de la demande metabolique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et tres succinctement de comprendre les modifications cardio-vasculaires induites par l'entrainement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
