SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons voir successivement les adaptations cardiovasculaires a l'exercice et a l'entrainement. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces modifications surviennent des que l'on  n'est plus au repos. le repos, sur le plan physiologique est la situation metabolique particuliere d'un sujet a jeun, au repos et en temperature ambiante neutre c'est a dire sans depense energetique de thermoregulation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
