SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme nous l'avons vu, le metabolisme musculaire depend dans la majorite des activites, du metabolisme aerobie. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;mesurer la quantite d'oxygene utilise par un sujet permet de quantifier l'intensite metabolique d'un type d'activite physique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on peut mesurer facilement la consommation d'oxygene d'une activite , le vo2, a l'aide d'un masque en quantifiant la difference d'oxygene entre l'inspiration et l'expiration. vous avez represente ici a gauche un coureur a pied effectuant une mesure de la puissance aerobie de son systeme lors d'un test d'effort a intensite croissante jusqu'a epuisement. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme vous pouvez le voir sur la figure de droite, la consommation d'oxygene augmente de facon lineaire, proportionnelle jusqu'a une valeur maximale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la consommation d'oxygene, le vo2 est proportionnel au debit cardiaque et a la quantite d'oxygene extraite par le muscle, que l'on appelle la davo2 ou difference arterio-veineuse en oxygene.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la quantite d'oxygene maximale qu'un sujet peut consommer represente la performance maximale aerobie de ce sujet. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un sujet masculin non sportif jeune passe ainsi de 3,5 ml/min/kg de vo2 a 40-45 ml/min/kg de vo2 max.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@la chaine de distribution de l'oxygene@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il n'y a pas de reserve en oxygene dans l'organisme (ou tres peu)&lt;/span&gt;&lt;span &gt;…&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;quand il y a besoin de plus d'oxygene pour repondre a la demande metabolique, il faut apporter plus d'oxygene. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez represente ici la chaine de distribution de l'oxygene depuis l'air ambiant a droite jusqu'a son utilisation dans le cycle de krebs mitochondrial. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;decrivons le circuit d'une molecule d'oxygene : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a l'inspiration la molecule d'oxygene est amenee au niveau des alveoles&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elle traverse la membrane alveolo-capillaire pour aller se fixer sur l'hemoglobine dans le capillaire pulmonaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le cœur et le systeme cardio-circulatoire pulmonaire et systemique vont l'amener jusqu'au niveau du muscle ou elle va etre extraite du capillaire pour etre utilisee dans la mitochondrie musculaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on retrouve la les deux determinants de la consommation d'oxygene :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le debit cardiaque et&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la difference arterioveineuse en oxygene.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sur ce schema, vous avez represente aussi le trajet inverse effectue par le co2, fruit du metabolisme musculaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a l'exercice, il faut donc augmenter la convection de l'oxygene donc augmenter :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le debit ventilatoire en jouant a la fois sur la frequence respiratoire et le volume courant&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le debit cardiaque en jouant sur la frequence cardiaque et le volume d'ejection systolique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de facon concomitante, il faut recruter des territoires vasculaires au niveau pulmonaire et dilater les arteres musculaires des muscles en activite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
