SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'elevation de la frequence cardiaque est permise&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par la levee du frein vagal qui permet instantanement d'augmenter la fc de 10 a 20 points avant que la stimulation sympathique n'entraine une augmentation de la fc mais aussi de l'&lt;/span&gt;&lt;span &gt;inotropisme&lt;/span&gt;&lt;span &gt;…&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la stimulation sympathique est a la fois d'origine centrale mais aussi par augmentation de la secretion de noradrenaline par les surrenales, comme vous pouvez le voir sur la figure du bas.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les catecholamines et la stimulation sympathique permettent aussi une dilatation bronchique qui permettra d'augmenter le debit ventilatoire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
