SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les besoins metaboliques d'un organe sont tres variables d'un organe a l'autre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez ici un tableau qui indique les besoins metaboliques de differents organes avec de gauche a droite, le % du debit cardiaque, le debit en local en ml/min et le debit normalise par rapport a la masse, que l'on appelle l'intensite metabolique. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'intensite metabolique permet de comparer differents organes entre eux.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous pouvez remarquer que les glandes surrenales et thyroides ont une intensite metabolique importante. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;inversement au repos, les muscles n'ont quasiment pas besoin d'apport sanguin et l'intensite metabolique est minime. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;certains organes ont un debit minimal relativement preserve &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; on les appelle lors organes nobles &lt;/span&gt;&lt;span &gt;-&lt;/span&gt;&lt;span &gt; c'est le cas par exemple du cœur et du cerveau.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@adaptation du systeme cv a l'exercice@";
SDpagedocid[SDpagenum] = SDdocnum;
