SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le phenomene de vasodilatation s'&lt;/span&gt;&lt;span &gt;autoentretient. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en effet l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;augmentation du debit dans la microcirculation (arterioles et metarterioles) produite par les controles metabolique et myogenique entraine une augmentation des forces de cisaillement au niveau de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;endothelium arteriel. c'est le shear stress dont nous avons parle.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en reponse les cellules endotheliales produisent des metabolites vasodilatateurs comme&lt;/span&gt;&lt;span &gt; le monoxyde d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;azote ou des prostacyclines.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce phenomene est baptise dilatation secondaire des grosses arteres.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
